// General
import React from "react";
import { useControl } from "./Context/ControlContext";
import 'bootstrap/dist/css/bootstrap.min.css';

// Pages
import Banner from "./components/Banner/Banner";
import Welcome from "./components/Welcome/Welcome";
import Video from "./components/Video/Video";
import Sound from "./components/Sound/Sound";

//Layout
import ButtonFullScreen from './components/FullScreenButton/ButtonFullScreen';

// Api
// import api from './Api';

function App() {

  const {time, welcomeControl} = useControl();

  React.useEffect(()=>{
    console.log(time.minutes);
  })

  if(welcomeControl === false){
    if(time.minutes > 0 && time.minutes <= 25){
      return (
        <>
          <Video />
          <ButtonFullScreen />
        </>
      );
    }
    else if(time.minutes > 25 && time.minutes <= 34){
      return (
        <>
          <Banner />
          <ButtonFullScreen />
        </>
      );
    }
    else if(time.minutes > 45 && time.minutes <= 59){
      return (
        <>
          <Sound />
          <ButtonFullScreen />
        </>
      );
    }
  }
  else{
    return (
      <>
        <Welcome />
        <ButtonFullScreen />
      </>
    );
  }

}




export default App;
